mapper.select:

select user_no   userNo,
       review_no reviewNo
from review_user
where user_no = #{userNo}
  and review_no = #{reviewNo}

mapper.delete:

delete
from review_user
where user_no = #{userNo}
  and review_no = #{reviewNo}

mapper.select:

select count(review_no) likecheck
from review
where user_no = #{userNo}
